import { getPeople, getCountries } from "./DataApi";
import { GetCounriesResponse } from "DataApi/country.interface";
import { People } from "DataApi/people.interface";
import React, { useEffect, useState, useCallback } from "react";
import debounce from "lodash.debounce";

const App: React.FunctionComponent = () => {
  const [people, setPeople] = useState<People[]>([]);
  console.log("~ people", people);
  const [foundResults, setFoundResults] = useState<number>(); // רועי
  const [totalResults, setTotalResults] = useState<number>(); // רועי

  useEffect(() => {
    firstRender();
  }, []);

  const firstRender = async () => {
    const getAllPeopleResult = await getPeople({});
    console.log("~ getAllPeopleResult", getAllPeopleResult);
    setPeople(getAllPeopleResult.searchResults);
    setFoundResults(getAllPeopleResult.searchResultCount); // רועי
    setTotalResults(getAllPeopleResult.totalResultCounter); //רועי
    const result: GetCounriesResponse = await getCountries({});
    console.log("countries", result);
  };

  const age = (birthday: string) => {
    let today = new Date();
    let birthdayYear = new Date(birthday).getFullYear();
    // console.log("~ birthdayYear", birthdayYear);
    let year = today.getFullYear();
    // console.log("~ year", year);

    return year - birthdayYear;
  };
  const [q, setQuery] = useState<string>("");
  console.log("q", q);
  const changeHandler = (event: any) => {
    setQuery(event.target.value);
  };

  const debouncedChangeHandler = useCallback(debounce(changeHandler, 3000), []);

  return (
    <div className="pageWrapper">
      <p>Search Component</p>
      <input
        type="search"
        onChange={(e: React.FormEvent<HTMLInputElement>) => {
          if (e?.currentTarget?.value.length > 2) {
            console.log(e?.currentTarget?.value);
            debouncedChangeHandler;
          } else {
            console.log("LESS THEN TWO CHARS");
          }
          // searchPeople(e?.currentTarget?.value); // רועי
        }}
      />
      <p>List Component</p>
      <div className="listWrapper">
        {people.map((obj: People) => {
          return (
            <div key={obj.id}>
              <p>
                {obj.first_name} {obj.last_name}
              </p>
              <p>{obj.country}</p>
              <p>{age(obj.date_of_birth)}</p>
              <img className="flag" height={50} src="Flag src" alt="Flag alt" />
            </div>
          );
        })}
      </div>
      <p>Found results: {foundResults} </p>
      <p>Total results: {totalResults}</p>
    </div>
  );
};

export default App;
